mapper.update:
		update joinmember
		set
		passwd=#{passwd}, email=#{email}, phone=#{phone},
		post1=#{post1},
		post2=#{post2}, addr1=#{addr1}, addr2=#{addr2}
		where userid=#{userid}

update board
set content=#{content}
where num=#{num}

mapper.select:
		
		select num, userid, ordername, phone, email, post1, post2, addr1, addr2, gcode, gname, gprice, gimage, paymethod,
		to_char(orderday, 'mm/dd') "orderday" , sellerid
		from order_info
		where userid=#{userId}
		
			and orderday between #{startdate} and #{finaldate}
		
		order by num desc

select count(*)
from order_info
where userid = #{userId}

	and orderday between #{startdate} and #{finaldate}
